package com.jobrecipe.review.vo;



import java.sql.Date;

public class ReviewVO {
	private int c_rev_no;
	private String c_name;
	private int u_no;
	private String c_rev_title;
	private String c_rev_content;
	private String c_rev_cnt;
	private String c_rev_star;
	private Date c_rev_date;

	public ReviewVO() {
		// TODO Auto-generated constructor stub
	}

	public int getC_rev_no() {
		return c_rev_no;
	}

	public void setC_rev_no(int c_rev_no) {
		this.c_rev_no = c_rev_no;
	}

	public String getC_name() {
		return c_name;
	}

	public void setC_name(String c_name) {
		this.c_name = c_name;
	}

	public int getU_no() {
		return u_no;
	}

	public void setU_no(int u_no) {
		this.u_no = u_no;
	}

	public String getC_rev_title() {
		return c_rev_title;
	}

	public void setC_rev_title(String c_rev_title) {
		this.c_rev_title = c_rev_title;
	}

	public String getC_rev_content() {
		return c_rev_content;
	}

	public void setC_rev_content(String c_rev_content) {
		this.c_rev_content = c_rev_content;
	}

	public String getC_rev_cnt() {
		return c_rev_cnt;
	}

	public void setC_rev_cnt(String c_rev_cnt) {
		this.c_rev_cnt = c_rev_cnt;
	}

	public String getC_rev_star() {
		return c_rev_star;
	}

	public void setC_rev_star(String c_rev_star) {
		this.c_rev_star = c_rev_star;
	}

	public Date getC_rev_date() {
		return c_rev_date;
	}

	public void setC_rev_date(Date c_rev_date) {
		this.c_rev_date = c_rev_date;
	}

}